function intermission (togo, money, remaining, increment )% function resting (togo, money)%% Displays resting screen% Inputs:%  togo - number of blocks to go%  money - money earned% remaining -- seconds remaining in intermission% increment -- duration of intervals between updates of the countdown timerlnsp = 80;windowptr = screen (0, 'OpenWindow', 255);screen (windowptr, 'TextSize', 24);screen (windowptr, 'TextFont', 'Courier New');% TextCenter (windowptr, 'Please now rest', -lnsp);% TextCenter (windowptr, 'Whenever you are ready to');% TextCenter (windowptr, 'continue, press SPACE.', lnsp);TextCenter (windowptr, 'Now there will be a 10 minute intermission,', -2*lnsp);TextCenter (windowptr, 'after which you will be prompted to continue', -lnsp);TextCenter (windowptr, 'by pressing SPACE.', 0);if nargin >= 1    TextCenter (windowptr, sprintf ('Number of blocks remaining: %d', togo), lnsp);endif nargin >= 2    TextCenter (windowptr, sprintf ('Total earned: $%.2f', money / 100), 2*lnsp);end% Now pause and print timer values:if remaining <= 60	TextCenter( windowptr, sprintf( 'Intermission time remaining: %d seconds', ...	floor(remaining) ), 3*lnsp );else	TextCenter( windowptr, sprintf( 'Intermission time remaining: %d minutes', ...	floor(remaining/60) ), 3*lnsp );end% % Rafal didn't test to make sure character was actually SPACE:% c = GetChar;% while strcmp( c, ' ' ) ~= 1% 	c = GetChar;% endpause(increment);% screen ('CloseAll');