function resting_arrow(togo, money, cue_type)%% function resting (togo, money)%% Displays resting screen% Inputs:%  togo - number of blocks to go%  money - money earnedstr = {};x_pos = {};str{1} = 'Please rest now.';if strcmp(cue_type,'a')==1    str{2} = 'In this block, wait til the ARROW appears.';else    str{2} = 'In this block, pay attention to DOT MOTION.';endstr{3} = 'Z for left ';str{4} = 'M for right';str{6} = sprintf ('Number of blocks remaining: %d', togo);str{7} = sprintf('Total earned: $%.2f', money/100);str{8} = '';str{9} = '(Press the ''Space'' key to continue.)';showText(str);