function thankyou (money, sig_det_score)% 7/17/07: Modified for DotsX.%% function thankyou (money, opponent_money)%% Displays screen thanking the subject for participation in the experiment% Input:%  money - the amount of money the subject earned%  sig_det_score -- the points scored by subject in signal detection blocksstr = {};str{1} = 'This is the end of the experiment.';str{2} = 'Thank you very much for ';str{3} = 'your participation!';str{4} = sprintf ('You have earned $%.2f.', money/100);str{5} = 'Congratulations!';showText(str);expdir = fileparts(which('experiment_eeg.m'));[y, freq, nbits] = wavread(fullfile(expdir,'TADA'));wavedata = y';nrchannels = size(wavedata,1); % Number of rows == number of channels.pahandle = PsychPortAudio('Open', [], [], 0, freq, nrchannels);PsychPortAudio('FillBuffer', pahandle, wavedata);tstart = GetSecs;repetitions = 1; % Just play it oncet1 = PsychPortAudio('Start', pahandle, repetitions, 0, 1);t2 = PsychPortAudio('Stop', pahandle,1);PsychPortAudio('Close',pahandle);dur = GetSecs - tstart;